create table student_data(student_name varchar(30) not null,father_name varchar(30) not null,mother_name varchar(30) not null,student_email_id varchar(20) not null,student_password varchar(15) not null,student_roll_number varchar(15) not null primary key,gender varchar(10) not null,address varchar(30) not null,city varchar(20) not null,parent_occupation varchar(20) not null,student_blood_group varchar(5) not null,student_standard varchar(4)not null,student_staff_name varchar(30)not null,parent_mobile_number varchar(10)not null,date_of_birth date not null, CONSTRAINT UC_student_data UNIQUE (student_email_id,student_password));